package ru.rrusanov.TDD;import java.util.HashMap;/** * @author Roman Rusanov * @version 0.1 * @since 4.08.2019 * * The class . */public class SimpleGenerator {    private HashMap<String, String> pairs = new HashMap<>();    private String template;    public void addPair(String key, String value) {        this.pairs.put(key, value);    }    public boolean removePair(String key, String value) {        return this.pairs.remove(key, value);    }    public String getPairValue(String key) {        return this.pairs.get(key);    }    public String getTemplate() {        return template;    }    public void setTemplate(String template) {        this.template = template;    }}